import { useEffect, useState, useRef } from 'react';

const App = () => {
  const [count, setCount] = useState(0);
  const [autoCount, setAutoCount] = useState(0);
  const [halfAutoCount, setHalfAutoCount] = useState(0);
  const intervalId = useRef(null);

  useEffect(()=>{
    const timer = setInterval(()=>setAutoCount((prev)=> prev+1),
    1000
    )
    return () => clearInterval(timer)
  },[count])

  const startCounter = () => {
    intervalId.current = setInterval(setHalfAutoCount((prev)=> prev + 1), 400)
  }

  const stopCounter = () => {
    clearInterval(intervalId.current);
  }
  return (
    <div className="App">
      <div>{count}</div>
      <button onClick={() => setCount((prev)=> prev + 1)}>클릭</button>    
      <div>{autoCount}</div>
      <div>{halfAutoCount}</div>
      <button onClick={startCounter}>시작</button>
      <button onClick={stopCounter}>종료</button>
    </div>
  );
}

export default App;
